#include <string>
#include <iostream>

using namespace std;

class Solution {
public:
    int longestBeautifulSubstring(string word) {
        const int word_len = word.size();
        int begin_index = 0;
        string str_vec = "aeiou";
        int str_vec_index = -1;
        int max_result = 0;
        for (int loop_i = 0; loop_i < word_len; ++loop_i) {
            if (str_vec_index == -1) {
                if (str_vec[0] == word[loop_i]) {
                    begin_index = loop_i;
                    str_vec_index = 0;
                } else {
                    continue;
                }
            }

            if (word[loop_i] == str_vec[str_vec_index]) {
                continue;
            } 

            if (str_vec_index != 4 && word[loop_i] == str_vec[str_vec_index + 1]) {
                str_vec_index++;
                continue;
            }

            if (str_vec_index == 4 && loop_i - begin_index > max_result) {
                max_result = loop_i - begin_index;
            }
            str_vec_index = -1;
            loop_i--;
        }
        if (str_vec_index == 4 && word_len - begin_index > max_result) {
            max_result = word_len - begin_index;
        }
        return max_result;
    }
};

int main() {
    std::cout << Solution().longestBeautifulSubstring("") << std::endl;
}